var charm = require('../')(process);

function exit () {
    charm.display('reset');
    process.exit();
}
charm.foreground('black');
charm.on('^C', exit);

var ix = 0;
var iv = setInterval(function () {
    charm.display('bright').background(ix++).write(' '+ ix.toString() + ' ');
    if (ix === 256) {
        clearInterval(iv);
        charm.write('\n');
        exit();
    }
}, 10);
